mapper.select:
SELECT cm_idx AS messageId, ch_idx AS roomId, sender_id AS senderId, message_type AS type, cm_content AS content, image_url AS imageUrl, cm_date AS time FROM chat_messages WHERE ch_idx = #{roomId}
AND cm_idx < #{before}
ORDER BY cm_idx ASC LIMIT #{size}

SELECT COUNT(*)
FROM chat_messages m
WHERE m.ch_idx = #{roomId}
  AND m.cm_idx > COALESCE((
                                 SELECT last_seen_message_id
                                 FROM chat_reads
                                 WHERE ch_idx = #{roomId} AND cread_reader = #{userId}
                             ), 0)
  AND COALESCE(m.sender_id, -1) != #{userId}
