mapper.select:

SELECT ch_idx FROM chat_rooms
WHERE ch_identifier = #{identifier}
    LIMIT 1

SELECT r.ch_idx AS roomId, p.pd_idx AS productId, COALESCE(p.pd_title, '(삭제된 상품)') AS productTitle,
COALESCE( p.pd_thumb, (SELECT pi.image_url FROM product_image pi WHERE pi.pd_idx = p.pd_idx ORDER BY pi.pi_idx LIMIT 1), '/images/placeholder.jpg' ) AS productThumb,
/* --- 판매자/구매자 판별 고정 --- */ COALESCE(d.seller_idx2, p.u_idx) AS sellerId, COALESCE( d.buyer_idx, (SELECT cr.cread_reader FROM chat_reads cr WHERE cr.ch_idx = r.ch_idx AND cr.cread_reader <> p.u_idx ORDER BY cr.cread_idx LIMIT 1) ) AS buyerId,
CASE WHEN COALESCE(d.seller_idx2, p.u_idx) = #{me} THEN '판매자' WHEN COALESCE( d.buyer_idx, (SELECT cr.cread_reader FROM chat_reads cr WHERE cr.ch_idx = r.ch_idx AND cr.cread_reader <> p.u_idx ORDER BY cr.cread_idx LIMIT 1) ) = #{me} THEN '구매자' ELSE '참여자' END AS myRole,
/* ✅ 판매 상태: d_status 기준 */ CASE WHEN COALESCE(d.d_status, 0) = 1 THEN '판매완료' ELSE '판매중' END AS saleStatus,
/* ✅ 표시 가격: 판매완료면 합의가 우선, 아니면 상품가 */ CASE WHEN COALESCE(d.d_status, 0) = 1 THEN COALESCE(d.agreed_price, p.pd_price) ELSE p.pd_price END AS displayPrice
FROM chat_rooms r LEFT JOIN product p ON p.pd_idx = r.pd_idx LEFT JOIN deal d ON d.d_idx = r.d_idx WHERE r.ch_idx = #{roomId} LIMIT 1

mapper.delete:

DELETE FROM chat_reads
WHERE ch_idx = #{roomId}
  AND cread_reader = #{userId}

mapper.select:
SELECT EXISTS(
    SELECT 1 FROM chat_reads
    WHERE ch_idx = #{roomId} AND cread_reader = #{userId}
    LIMIT 1
)
